import React, { createContext, FC, useContext, useEffect, useState } from 'react';
import { PagingList } from '../../services/base/paginate';
import { getResidentsList } from '../../services/resident';

import { Resident } from '../../services/resident/model';





const ResidentContext = createContext<PagingList<Resident> | null>(null);

export const ResidentProvider: FC = ({ children }) => {

  const residences = getResidentsList();

  return <ResidentContext.Provider value={residences}>{children}</ResidentContext.Provider>;
};

export const useResidencesList = () => {
  const list = useContext(ResidentContext);
  if (!list) {
    throw new Error('useUserList must be used inside ResidentProvider');
  }

  return list;
};
