import React, { FC, useEffect, useState } from "react";
import {
  Button,
  Form,
  Input,
  Image,
  Select,
  Switch,
  Upload,
  message,
  DatePicker,
  Col,
  Row,
} from "antd";
import { FormInstance } from "antd/es/form";
import { Resident } from "../../services/resident/model";
import { UploadOutlined } from "@ant-design/icons";
import { diskStorage } from "multer";
import ResidencesService from "../../services/resident";
import { post } from "../../services/base/fetch";
import moment from "moment";
import dayjs from "../../utils/dayjs";
import client from "../../services/base/client";

const RenderImage = (props: any) => {
  if (props.image === null || props.image === undefined) {
    return (
      <Upload
        name="image"
        listType="picture"
        action={"/api/resident1/image"}
        onChange={props.onChange}
      >
        <Button icon={<UploadOutlined />}>Click to upload</Button>
      </Upload>
    );
  } else {
    if (props.image.includes(".png")) {
      return <Image src={require(`./images/${props.image}`).default} />;
    } else {
      return (
        <Upload
          name="image"
          listType="picture"
          action={"/api/resident1/image"}
          onChange={props.onChange}
        >
          <Button icon={<UploadOutlined />}>Click to upload</Button>
        </Upload>
      );
    }
  }
};

const ResidentForm: FC<{ form: FormInstance; obj?: Resident }> = ({
  form,
  obj,
}) => {
  const [imagePath, setImagePath] = useState("");

  const onChange = (info: any) => {
    if (info.file.status !== "uploading") {
      console.log(info.file, info.fileList);
    }
    if (info.file.status === "done") {
      message.success(`${info.file.name} file uploaded successfully`);
      setImagePath(info.file.name);
    } else if (info.file.status === "error") {
      message.error(`${info.file.name} file upload failed.`);
    }
  };

  useEffect(() => {
    form.setFieldsValue({ image: imagePath });
  }, [imagePath]);

  useEffect(() => {
    if (obj) {
      console.log(obj.ngay_vao_dang);
      form.setFieldsValue(obj);
      setImagePath(obj.image);
    } else {
      form.resetFields();
    }
  }, [obj]);

  return (
    <Form form={form} labelCol={{ flex: "0 0 90px" }}>
      <Form.Item name="id" hidden={true}>
        <Input disabled={true} />
      </Form.Item>
      <Row gutter={24}>
        <Col span={12}>
          <RenderImage image={obj?.image} onChange={onChange} />
          <Form.Item name="image">
            <Input value={imagePath} hidden />
          </Form.Item>
        </Col>
        <Col span={12}>
          <Form.Item
            label="Họ Và Tên"
            name="ho_ten"
            rules={[{ required: true, message: "Hãy nhập họ Và tên!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item label="SHSQ" name="shsq">
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
            label="Tuổi"
            name="age"
            rules={[{ required: true, message: "Hãy nhập tuổi!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
            label="Năm sinh"
            name="nam_sinh"
            rules={[{ required: true, message: "Hãy nhập năm sinh!" }]}
          >
            <Input type="date" />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
            label="Quê quán"
            name="que_quan"
            rules={[{ required: true, message: "Hãy nhập quê quán!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
            label="Trú quán"
            name="tru_quan"
            rules={[{ required: true, message: "Hãy nhập trú quán!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
            label="Địa chỉ"
            name="dia_chi"
            rules={[{ required: true, message: "Hãy nhập địa chỉ!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
