import React, { FC, useEffect, useState } from "react";
import {
  Button,
  Form,
  Input,
  Image,
  Select,
  Switch,
  Upload,
  message,
  DatePicker,
  Col,
  Row,
} from "antd";
import { FormInstance } from "antd/es/form";
import { Resident } from "../../services/resident/model";
import { UploadOutlined } from "@ant-design/icons";
import { diskStorage } from "multer";
import ResidencesService from "../../services/resident";
import { post } from "../../services/base/fetch";
import moment from "moment";
import dayjs from "../../utils/dayjs";
import client from "../../services/base/client";

async function postData(url = "", data) {
  // Default options are marked with *
  const response = await fetch(url, {
    method: "POST", // *GET, POST, PUT, DELETE, etc.
    mode: "cors", // no-cors, *cors, same-origin
    cache: "no-cache", // *default, no-cache, reload, force-cache, only-if-cached
    credentials: "same-origin", // include, *same-origin, omit
    headers: {
      // "Content-Type": "application/json",
      "Content-Type": "application/x-www-form-urlencoded",
    },
    redirect: "follow", // manual, *follow, error
    referrerPolicy: "no-referrer", // no-referrer, *no-referrer-when-downgrade, origin, origin-when-cross-origin, same-origin, strict-origin, strict-origin-when-cross-origin, unsafe-url
    body: data, // body data type must match "Content-Type" header
  });
  return response.json(); // parses JSON response into native JavaScript objects
}

const onChange = async ({ file }: any) => {
  const formData = new FormData();
  formData.append("image", file);
  const res = await postData("api/resident/image", {
    image: file,
  }).then((data) => {
    console.log(data); // JSON data parsed by `data.json()` call
  });
  console.log(res);
};

const RenderImage = (props: any) => {
  if (props.image === null || props.image === undefined) {
    return (
      <Upload name="logo" listType="picture" onChange={onChange}>
        <Button icon={<UploadOutlined />}>Click to upload</Button>
      </Upload>
    );
  } else {
    if (props.image.includes(".png")) {
      return <Image src={require(`./images/${props.image}`).default} />;
    } else {
      return (
        <Upload
          name="logo"
          listType="picture"
          action={"/api/resident"}
          onChange={onChange}
        >
          <Button icon={<UploadOutlined />}>Click to upload</Button>
        </Upload>
      );
    }
  }
};

const ResidentForm: FC<{ form: FormInstance; obj?: Resident }> = ({
  form,
  obj,
}) => {
  const normFile = (e: any) => {
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
  };

  useEffect(() => {
    if (obj) {
      console.log(obj.ngay_vao_dang);

      form.setFieldsValue(obj);
    } else {
      form.resetFields();
    }
  }, [obj]);

  return (
    <Form form={form} labelCol={{ flex: "0 0 90px" }}>
      <Form.Item name="id" hidden={true}>
        <Input disabled={true} />
      </Form.Item>
      <Row gutter={24}>
        <Col span={12}>
          <Form.Item
            name="image"
            label="Hình ảnh"
            valuePropName="fileList"
            extra="Hình nhận diện"
            getValueFromEvent={normFile}
          >
            <RenderImage image={obj?.image} />
          </Form.Item>
        </Col>
        <Col span={12}>
          <Form.Item
            label="Họ Và Tên"
            name="ho_ten"
            rules={[{ required: true, message: "Hãy nhập họ Và tên!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
