import React, { FC, useEffect, useState } from "react";
import {
  Button,
  Form,
  Input,
  Image,
  Select,
  Switch,
  Upload,
  message,
  DatePicker,
  Col,
  Row,
} from "antd";
import { FormInstance } from "antd/es/form";
import { Resident } from "../../services/resident/model";
import { UploadOutlined } from "@ant-design/icons";
import { diskStorage } from "multer";
import ResidencesService from "../../services/resident";
import { post } from "../../services/base/fetch";
import moment from "moment";
import dayjs from "../../utils/dayjs";
import client from "../../services/base/client";

const RenderImage = (props: any) => {
  if (props.image === null || props.image === undefined) {
    return (
      <Upload name="logo" listType="picture" action={"/api/resident/image"}>
        <Button icon={<UploadOutlined />}>Click to upload</Button>
      </Upload>
    );
  } else {
    if (props.image.includes(".png")) {
      return <Image src={require(`./images/${props.image}`).default} />;
    } else {
      return (
        <Upload name="logo" listType="picture" action={"/api/resident/image"}>
          <Button icon={<UploadOutlined />}>Click to upload</Button>
        </Upload>
      );
    }
  }
};

const ResidentForm: FC<{ form: FormInstance; obj?: Resident }> = ({
  form,
  obj,
}) => {
  const normFile = (e: any) => {
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
  };

  useEffect(() => {
    if (obj) {
      console.log(obj.ngay_vao_dang);

      form.setFieldsValue(obj);
    } else {
      form.resetFields();
    }
  }, [obj]);

  return (
    <Form form={form} labelCol={{ flex: "0 0 90px" }}>
      <Form.Item name="id" hidden={true}>
        <Input disabled={true} />
      </Form.Item>
      <Row gutter={24}>
        <Col span={12}>
          <Form.Item
            name="image"
            label="Hình ảnh"
            valuePropName="fileList"
            extra="Hình nhận diện"
            getValueFromEvent={normFile}
          >
            <RenderImage image={obj?.image} />
          </Form.Item>
        </Col>
        <Col span={12}>
          <Form.Item
            label="Họ Và Tên"
            name="ho_ten"
            rules={[{ required: true, message: "Hãy nhập họ Và tên!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item label="SHSQ" name="shsq">
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
            label="Tuổi"
            name="age"
            rules={[{ required: true, message: "Hãy nhập tuổi!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
            label="Năm sinh"
            name="nam_sinh"
            rules={[{ required: true, message: "Hãy nhập năm sinh!" }]}
          >
            <Input type="date" />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
            label="Quê quán"
            name="que_quan"
            rules={[{ required: true, message: "Hãy nhập quê quán!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
            label="Trú quán"
            name="tru_quan"
            rules={[{ required: true, message: "Hãy nhập trú quán!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
            label="Địa chỉ"
            name="dia_chi"
            rules={[{ required: true, message: "Hãy nhập địa chỉ!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item label="Văn hoá" name="van_hoa">
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item label="Đảng" name="dang">
            {obj?.dang == true || obj?.dang == false ? (
              <Switch defaultChecked={obj?.dang} />
            ) : (
              <Switch defaultChecked={false} />
            )}
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item label="Đoàn viên" name="doan_vien">
            {obj?.doan_vien == true || obj?.doan_vien == false ? (
              <Switch defaultChecked={obj?.doan_vien} />
            ) : (
              <Switch defaultChecked={false} />
            )}
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item label="Vào đảng" name="ngay_vao_dang">
            <Input type="date" />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item label="Tôn giáo" name="ton_giao">
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
