import React, { FC, useState } from "react";
import { Form, message, Modal } from "antd";
import { useRecoilState, useRecoilValue } from "recoil";
import { selectedResidentState, showResidentModalState } from "./states";
import { Resident } from "../../services/resident/model";
import ResidencesService, { useResidentsList } from "../../services/resident";
import ResidentForm from "./form";

const ResidentModal: FC = () => {
  const { fetch } = useResidentsList();
  const [showModal, setShowModal] = useRecoilState(showResidentModalState);
  const selectedResident = useRecoilValue(selectedResidentState);
  const [loading, setLoading] = useState(false);
  const [form] = Form.useForm();

  const handleClose = () => {
    setShowModal(false);
  };

  const title = selectedResident ? "Sửa thông tin" : "Tạo mới";

  const handleSubmit = () => {
    form
      .validateFields()
      .then(
        (value) => {
          if(title === "Sửa thông tin") {
            setLoading(true);
          return ResidencesService.update(value as Resident);
          }
          setLoading(true);
          return ResidencesService.update(value as Resident);
        },
        (err) => {
          message.error(err.errorFields[0].errors[0]);
        }
      )
      .then(async (res) => {
        setLoading(false);
        if (res && res.ok) {
          message.success(`${title} successfully`);
          setShowModal(false);
          fetch();
        }
      });
  };

  return (
    <Modal
      title={title}
      visible={showModal}
      closable
      onCancel={handleClose}
      onOk={handleSubmit}
      okButtonProps={{ loading }}
    >
      <ResidentForm form={form} obj={selectedResident} />
    </Modal>
  );
};

export default ResidentModal;
