import React, { FC, useEffect, useState } from "react";
import { Button, Form, Input, Image, Select, Switch, Upload, message } from "antd";
import { FormInstance } from "antd/es/form";
import { Resident } from "../../services/resident/model";
import { UploadOutlined } from "@ant-design/icons";

const ResidentForm: FC<{ form: FormInstance; obj?: Resident }> = ({
  form,
  obj,
}) => {
  const normFile = (e: any) => {
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
  };

  const beforeUpload = (file:any) => {
    const isJpgOrPng = file.type === 'image/jpeg' || file.type === 'image/png';
    if (!isJpgOrPng) {
      message.error('You can only upload JPG/PNG file!');
    }
    const isLt2M = file.size / 1024 / 1024 < 2;
    if (!isLt2M) {
      message.error('Image must smaller than 2MB!');
    }
    return isJpgOrPng && isLt2M;
  }

  useEffect(() => {
    if (obj) {
      form.setFieldsValue(obj);
    } else {
      form.resetFields();
    }
  }, [obj]);

  return (
    <Form form={form} labelCol={{ flex: "0 0 90px" }}>
      <Form.Item
        name="image"
        label="Hình ảnh"
        valuePropName="fileList"
        extra="Hình nhận diện"
        getValueFromEvent={normFile}
      >
        {obj?.image !== undefined ? (
          <Image src={obj.image} />
        ) : (
          <Upload name="logo" action="/api/resident/image"  beforeUpload={beforeUpload} listType="picture">
            <Button icon={<UploadOutlined />}>Click to upload</Button>
          </Upload>
        )}
      </Form.Item>
      <Form.Item
        label="Họ Và Tên"
        name="ho_ten"
        rules={[{ required: true, message: "Please input name!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item label="SHSQ" name="shsq">
        <Input />
      </Form.Item>
      <Form.Item label="Đảng" name="dang">
        <Switch defaultChecked={obj?.dang} />
      </Form.Item>
      <Form.Item label="Đoàn viên" name="doan_vien">
        <Switch defaultChecked={obj?.doan_vien} />
      </Form.Item>
    </Form>
  );
};

export default ResidentForm;
