import React, { FC, useEffect, useState } from "react";
import {
  Button,
  Form,
  Input,
  Image,
  Select,
  Switch,
  Upload,
  message,
  DatePicker,
  Col,
  Row,
} from "antd";
import { FormInstance } from "antd/es/form";
import { Resident } from "../../services/resident/model";
import { UploadOutlined } from "@ant-design/icons";
import { diskStorage } from "multer";
import ResidencesService from "../../services/resident";
import { post } from "../../services/base/fetch";
import moment from "moment";
import dayjs from "../../utils/dayjs";

const onChange = async ({ file }: any) => {
  console.log(file);
  const res = await post("/api/resident/image", {
    image: file,
  } as any);
  console.log(res);
};

const request = async <T = any, E = any>(
  path: string,
  method: string,
  { secure, ...params }: RequestParams = {},
  body?: any,
  bodyType?: BodyType,
  secureByDefault?: boolean
): Promise<HttpResponse<T, E>> => {
  const requestUrl = `${this.baseUrl}${path}`;
  const secureOptions =
    (secureByDefault || secure) && this.securityWorker
      ? this.securityWorker(this.securityData)
      : {};
  let options = this.mergeRequestOptions(params, secureOptions);
  if (this.interceptors.request) {
    options = await Promise.resolve(this.interceptors.request({ options }));
  }
  const requestOptions = {
    ...options,
    method,
    body: body ? this.bodyFormatters[bodyType || BodyType.Json](body) : null,
  };

  return fetch(requestUrl, requestOptions).then(async (response) => {
    let data = await this.safeParseResponse<T, E>(response);
    if (this.interceptors.response) {
      data = await this.interceptors.response<T, E>({ response: data });
    }
    // if (!response.ok) throw data;
    return data;
  });
};

const RenderImage = (props: any) => {
  if (props.image === null || props.image === undefined) {
    return (
      <Upload name="logo" listType="picture" onChange={onChange}>
        <Button icon={<UploadOutlined />}>Click to upload</Button>
      </Upload>
    );
  } else {
    if (props.image.includes(".png")) {
      return <Image src={require(`./images/${props.image}`).default} />;
    } else {
      return (
        <Upload
          name="logo"
          listType="picture"
          action={"/api/resident"}
          onChange={onChange}
        >
          <Button icon={<UploadOutlined />}>Click to upload</Button>
        </Upload>
      );
    }
  }
};

const ResidentForm: FC<{ form: FormInstance; obj?: Resident }> = ({
  form,
  obj,
}) => {
  const normFile = (e: any) => {
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
  };

  useEffect(() => {
    if (obj) {
      console.log(obj.ngay_vao_dang);

      form.setFieldsValue(obj);
    } else {
      form.resetFields();
    }
  }, [obj]);

  return (
    <Form form={form} labelCol={{ flex: "0 0 90px" }}>
      <Form.Item name="id" hidden={true}>
        <Input disabled={true} />
      </Form.Item>
      <Row gutter={24}>
        <Col span={12}>
          <Form.Item
            name="image"
            label="Hình ảnh"
            valuePropName="fileList"
            extra="Hình nhận diện"
            getValueFromEvent={normFile}
          >
            <RenderImage image={obj?.image} />
          </Form.Item>
        </Col>
        <Col span={12}>
          <Form.Item
            label="Họ Và Tên"
            name="ho_ten"
            rules={[{ required: true, message: "Hãy nhập họ Và tên!" }]}
          >
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item label="SHSQ" name="shsq">
            <Input />
          </Form.Item>
        </Col>
        <Col span={8}>
          <Form.Item
