import React, { FC, useEffect, useState } from "react";
import {
  Button,
  Form,
  Input,
  Image,
  Select,
  Switch,
  Upload,
  message,
  DatePicker,
  Col,
} from "antd";
import { FormInstance } from "antd/es/form";
import { Resident } from "../../services/resident/model";
import { UploadOutlined } from "@ant-design/icons";
import { diskStorage } from "multer";
import ResidencesService from "../../services/resident";
import { post } from "../../services/base/fetch";
import moment from "moment";
import dayjs from "../../utils/dayjs";

const ResidentForm: FC<{ form: FormInstance; obj?: Resident }> = ({
  form,
  obj,
}) => {
  const normFile = (e: any) => {
    if (Array.isArray(e)) {
      return e;
    }
    return e && e.fileList;
  };

  useEffect(() => {
    if (obj) {
      form.setFieldsValue(obj);
    } else {
      form.resetFields();
    }
  }, [obj]);

  const onChange = async ({ file }: any) => {
    console.log(file);
    const res = await post("/api/resident/image", {
      image: file,
    } as any);
    console.log(res);
  };

  return (
    <Form form={form} labelCol={{ flex: "0 0 90px" }}>
      <Form.Item name="id" hidden={true}>
        <Input disabled={true} />
      </Form.Item>
      <Col span={8}>
        <Form.Item
          name={`field-${i}`}
          label={`Field ${i}`}
          rules={[
            {
              required: true,
              message: 'Input something!',
            },
          ]}
        >
          <Input placeholder="placeholder" />
        </Form.Item>
      </Col>
      <Form.Item
        name="image"
        label="Hình ảnh"
        valuePropName="fileList"
        extra="Hình nhận diện"
        getValueFromEvent={normFile}
      >
        {obj?.image !== undefined ? (
          <Image src={require(`./images/${obj?.image}`).default} />
        ) : (
          <Upload
            name="logo"
            listType="picture"
            action={"https://www.mocky.io/v2/5cc8019d300000980a055e76"}
            onChange={onChange}
          >
            <Button icon={<UploadOutlined />}>Click to upload</Button>
          </Upload>
        )}
      </Form.Item>
      <Form.Item
        label="Họ Và Tên"
        name="ho_ten"
        rules={[{ required: true, message: "Please input name!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item label="SHSQ" name="shsq">
        <Input />
      </Form.Item>
      <Form.Item label="Tuổi" name="age">
        <Input />
      </Form.Item>
      <Form.Item label="Năm sinh" name="nam_sinh">
        <Input type="date" value={dayjs(obj?.nam_sinh).format("DD/MM/YYYY")} />
      </Form.Item>
      <Form.Item label="Quê quán" name="que_quan">
        <Input />
      </Form.Item>
      <Form.Item label="Trú quán" name="tru_quan">
        <Input />
      </Form.Item>
      <Form.Item label="Địa chỉ" name="dia_chi">
        <Input />
      </Form.Item>
      <Form.Item label="Văn hoá" name="van_hoa">
        <Input />
      </Form.Item>
      <Form.Item label="Đảng" name="dang">
        <Switch defaultChecked={obj?.dang} />
      </Form.Item>
      <Form.Item label="Đoàn viên" name="doan_vien">
        <Switch defaultChecked={obj?.doan_vien} />
      </Form.Item>
      <Form.Item label="Vào đảng" name="ngay_vao_dang">
        <Input type="date" value={dayjs(obj?.nam_sinh).format("DD/MM/YYYY")} />
      </Form.Item>
      <Form.Item label="Tôn giáo" name="ton_giao">
        <Input />
      </Form.Item>
      <Form.Item label="Sức khoẻ" name="suc_khoe">
        <Input />
      </Form.Item>
      <Form.Item label="Nơi làm việc" name="noi_lam_viec">
        <Input />
      </Form.Item>
      <Form.Item label="SĐT" name="so_dien_thoai">
        <Input />
      </Form.Item>
      <Form.Item label="Nhập ngũ" name="nhap_ngu">
        <Input type="date" value={dayjs(obj?.nam_sinh).format("DD/MM/YYYY")} />
      </Form.Item>
      <Form.Item label="Xuất ngũ" name="xuat_ngu">
        <Input type="date" value={dayjs(obj?.nam_sinh).format("DD/MM/YYYY")} />
      </Form.Item>
      <Form.Item label="Tên đơn vị" name="ten_don_vi">
        <Input />
      </Form.Item>
      <Form.Item label="Cấp bậc" name="cap_bac">
        <Input />
      </Form.Item>
      <Form.Item label="Chức vụ" name="chuc_vu">
        <Input />
      </Form.Item>
    </Form>
  );
};

export default ResidentForm;
