import React, { FC } from 'react';
import { Button, message, Modal } from 'antd';
import { DeleteOutlined, EditOutlined, ExclamationCircleOutlined } from '@ant-design/icons';
import { Resident } from '../../services/resident/model';
import { useResidencesList } from './context';
import { useSetRecoilState } from 'recoil';
import { selectedResidentState, showResidentModalState } from './states';
import ResidencesService from '../../services/resident';



const ResidentListAction: FC<{ resident: Resident }> = ({ resident }) => {

  const { fetch } = useResidentsList();
  const setShowForm = useSetRecoilState(showResidentModalState);
  const setSelectedResident = useSetRecoilState(selectedResidentState);


  const handleEdit = () => {
    setShowForm(true);
    setSelectedResident(resident);
  };

  const handleDelete = () => {
    Modal.confirm({
      icon: <ExclamationCircleOutlined />,
      content: `Delete blueprint ${resident.id}?`,
      okButtonProps: { danger: true },
      onOk: async () => {
        const res = await ResidencesService.destroy(resident.id);
        if (res.ok) {
          message.success('Deleted successfully');
          fetch();
        }
      },
    });
  };

  return (
    <Button.Group>
      <Button type="primary" icon={<EditOutlined />} onClick={handleEdit} />
      <Button danger icon={<DeleteOutlined />} onClick={handleDelete} />
    </Button.Group>
  );
};

export default ResidentListAction;
