import React, { FC, useEffect, useState } from "react";
import { Form, Input, message, Select, Switch, Upload } from "antd";
import { FormInstance } from "antd/es/form";
import { Resident } from "../../services/resident/model";

const ResidentForm: FC<{ form: FormInstance; obj?: Resident }> = ({
  form,
  obj,
}) => {
  const props = {
    name: "file",
    action: "https://www.mocky.io/v2/5cc8019d300000980a055e76",
    headers: {
      authorization: "authorization-text",
    },
    onChange(info: { file: { status: string; name: any }; fileList: any }) {
      if (info.file.status !== "uploading") {
        console.log(info.file, info.fileList);
      }
      if (info.file.status === "done") {
        message.success(`${info.file.name} file uploaded successfully`);
      } else if (info.file.status === "error") {
        message.error(`${info.file.name} file upload failed.`);
      }
    },
  };

  useEffect(() => {
    if (obj) {
      form.setFieldsValue(obj);
    } else {
      form.resetFields();
    }
  }, [obj]);

  return (
    <Form form={form} labelCol={{ flex: "0 0 90px" }}>
      <Form.Item label="Hình ảnh" name="image">
        <Upload {...props}>
          <Button icon={<UploadOutlined />}>Click to Upload</Button>
        </Upload>
        ,
      </Form.Item>
      <Form.Item
        label="Họ Và Tên"
        name="ho_ten"
        rules={[{ required: true, message: "Please input name!" }]}
      >
        <Input />
      </Form.Item>
      <Form.Item label="SHSQ" name="shsq">
        <Input />
      </Form.Item>
      <Form.Item label="Đảng" name="dang">
        <Switch defaultChecked={obj?.dang} />
      </Form.Item>
      <Form.Item label="Đoàn viên" name="doan_vien">
        <Switch defaultChecked={obj?.doan_vien} />
      </Form.Item>
    </Form>
  );
};

export default ResidentForm;
